import React,{useState,useEffect} from "react"
import ToastComponent from "../common/controls/newtoast";


function CreateTicket({User,onEvent}){
  const [Message, SetMessage] = useState({ message: "", type: "" });
  const [showToast, setShowToast] = useState(false);

  const [RequestModel, SetRequestModel] = useState({
     _id: "",
     RequestTitle:"",
     RequestType:"",
     RequestQuery:"",
     UserId:""
  });

  const sendValueToParent = (value) => {
    onEvent(value);
  };
  const handleShowToast = () => {
    setShowToast(true);
  };

  const handleCloseToast = () => {
    setShowToast(false);
  };

  useEffect(() => {
    SetRequestModel({
      name: User?.UserId,
      email: User?.RequestType,
      IsAccountLocked: User?.RequestTitle,
      IsEmailVerified: User?.RequestQuery,
      _id: User?._id
    });
  }, [User?._id]);

  const handleChange = (field, value, call) => {
    if (call == undefined) {
      call = true;
    }
    if (call == true) {
      if (field === "RequestTitle" && call) {
        //SendMailModel.FromEmail = value;
        SetRequestModel((prev) => ({
          ...prev,
          RequestTitle: value
        }));
      }
      if (field === "RequestType" && call) {
        //SendMailModel.ToEmail = value;
        SetRequestModel((prev) => ({
          ...prev,
          RequestType: value
        }));
      }
      if (field === "RequestQuery" && call) {
        //SendMailModel.Subject = value;
        SetRequestModel((prev) => ({
          ...prev,
          RequestQuery: value
        }));
      }

      // SetUserModel(UserModel)
    }
  };

  const HandleSubmit = async (UserModel) => {
    if (UserModel) {
      if (!(UserModel?.Request && UserModel?.FromEmail != "")) {
        SetMessage({
          message: "From Email cannot be blank",
          type: "text-danger"
        });
        handleShowToast();
        return;
      }
      if (!(UserModel?.ToEmail && UserModel?.ToEmail != "")) {
        SetMessage({
          message: "To Email cannot be blank",
          type: "text-danger"
        });
        handleShowToast();
        return;
      }
      if (!(UserModel?.Subject && UserModel?.Subject != "")) {
        SetMessage({
          message: "Subject cannot be blank",
          type: "text-danger"
        });
        handleShowToast();
        return;
      }
      if (!(UserModel?.Body && UserModel?.Body != "")) {
        SetMessage({
          message: "Message Body cannot be blank",
          type: "text-danger"
        });
        handleShowToast();
        return;
      }

      const result = {}//await SendMailLog(UserModel);
      if (result && result?.message && result?.message != "") {
        SetMessage({
          message: result.message,
          type: result?.message.includes("1:") ? "text-success" : "text-danger"
        });
        handleShowToast();
        setTimeout(() => {
          if (result?.message.includes("1:")) {
            sendValueToParent(true);
          }
        }, 500);
      }
    }
  };
  return(
    <>
      <div
        className="position-fixed top-2 end-0 p-3"
        style={{ zIndex: 11, marginTop: "-55px" }}
      >
        <ToastComponent
          show={showToast}
